INSERT INTO `wp_posts` VALUES (375,1,'2015-09-14 22:00:28','2015-09-14 14:00:28','<ul>\n	<li><a href=\"http://academic.ntue.edu.tw/files/11-1007-103.php\" target=\"_blank\" rel=\"noopener\">前往教務處課務組</a>\n<ul>\n	<li>人工加退選注意事項 (含申請流程圖)</li>\n	<li>校外教學參觀公文申請表 (含申請流程圖)</li>\n	<li>校際選課申請單 (含申請流程圖)</li>\n	<li>修習學程申請書</li>\n</ul>\n</li>\n	<li><a href=\"/2015dtd/wp-content/uploads/2015/09/ac1-1.doc\" target=\"_blank\" rel=\"noopener\">國立臺北教育大學提昇學生英語能力實施辦法</a></li>\n</ul>','教務處課務組[適用日間部學制]','','publish','open','open','','%e6%95%99%e5%8b%99%e8%99%95%e8%aa%b2%e5%8b%99%e7%b5%84%e9%81%a9%e7%94%a8%e6%97%a5%e9%96%93%e9%83%a8%e5%ad%b8%e5%88%b6','','','2015-09-14 22:00:28','2015-09-14 14:00:28','',0,'http://localhost:8080/wordpress/?p=375',0,'post','',0);
INSERT INTO `wp_posts` VALUES (378,1,'2015-09-14 22:28:22','2015-09-14 14:28:22','<ul>\n	<li><a href=\"http://academic.ntue.edu.tw/files/11-1007-90.php\" target=\"_blank\" rel=\"noopener\">前往教務處招生註冊組</a>\n<ul>\n	<li> 各類證明文件申請作業流程及收費標準</li>\n	<li>大學部辦理就學貸款申請開立繳費單申請表</li>\n	<li>中文成績單申請表 (含申請流程圖)</li>\n	<li>休學申請表 (含申請流程圖)</li>\n	<li>成績排名申請表</li>\n	<li>抵免學分申請表 (含申請流程圖)</li>\n	<li>研究生辦理就學貸款申請開立繳費單申請表 (含申請流程圖)</li>\n	<li>研究生離校手續申請表 (含申請流程圖)</li>\n	<li>修業證明書申請表 (含申請流程圖)</li>\n	<li>修讀輔系申請表</li>\n	<li>修讀雙主修申請表</li>\n	<li>補開繳費單申請表</li>\n	<li>輔系學分審核表 (含申請流程圖)</li>\n	<li>學生論文及報告之著作權歸屬聲明書</li>\n	<li>學生證補發申請表 (含申請流程圖)</li>\n	<li>轉系申請表 (含申請流程圖)</li>\n	<li>轉組申請表</li>\n	<li>雙主修學分審核表</li>\n</ul>\n</li>\n</ul>','教務處招生 (含學術服務)組 [適用日間部學制]','','publish','open','open','','%e6%95%99%e5%8b%99%e8%99%95%e6%8b%9b%e7%94%9f-%e5%90%ab%e5%ad%b8%e8%a1%93%e6%9c%8d%e5%8b%99%e7%b5%84-%e9%81%a9%e7%94%a8%e6%97%a5%e9%96%93%e9%83%a8%e5%ad%b8%e5%88%b6','','','2015-09-14 22:28:22','2015-09-14 14:28:22','',0,'http://localhost:8080/wordpress/?p=378',0,'post','',0);
INSERT INTO `wp_posts` VALUES (383,1,'2015-09-15 13:37:16','2015-09-15 05:37:16','[vc_row][vc_column][vc_column_text]\n<h2 style=\"text-align: center;\"><strong>Shadow Circus</strong></h2>\n<h3 style=\"text-align: center;\">巫家瑜 | 林孟穎 | 賴韻婷 | 黃郁景</h3>\n\n<hr />\n\n<h3 style=\"text-align: center;\"></h3>\n<h2 style=\"text-align: center;\"><strong>遊戲概觀</strong></h2>\n<h4 style=\"text-align: center;\">在色彩繽紛由動物所組成的馬戲團裡，團員們皆擁有操控影子的能力，</h4>\n<h4 style=\"text-align: center;\">可以將不同材料的影子排列組合，組合出的物品影子，可將此實體化成為可使用道具。</h4>\n<h4 style=\"text-align: center;\">利用光與影的特性、豐富的材料變化、多元的物品提示，創造出別出心裁的世界。</h4>\n<h4 style=\"text-align: center;\">遊戲風格為可愛逗趣，帶給玩家歡樂的氛圍。</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>遊戲類型</strong></h2>\n<h4 style=\"text-align: center;\">冒險益智遊戲</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>設計目標</strong></h2>\n<h4 style=\"text-align: center;\">結合冒險與益智兩種遊戲類型特色，給玩家有別於單一類型的遊戲體驗，</h4>\n<h4 style=\"text-align: center;\">利用簡單光與影的原理，搭配多變的材料組合，增加遊戲的豐富度與趣味性。</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>故事背景</strong></h2>\n<h4 style=\"text-align: center;\">主角卡樂弗是一隻富有好奇心，</h4>\n<h4 style=\"text-align: center;\">但有缺陷的變色龍，他因為這個缺陷時常被同伴嘲笑。</h4>\n<h4 style=\"text-align: center;\">有一天，他看見了一場精采絕倫的馬戲團表演，</h4>\n<h4 style=\"text-align: center;\">期望能和表演者們一樣厲害，所以決定偷偷潛入馬戲團裡一探究竟。</h4>\n[/vc_column_text][/vc_column][/vc_row][vc_row][vc_column][vc_btn title=\"回上一頁\" color=\"info\" link=\"url:http%3A%2F%2Fdtd.ntue.edu.tw%2F%25E4%25BD%259C%25E5%2593%2581%25E5%25B1%2595%25E7%25A4%25BA%2F||\"][/vc_column][/vc_row]','Shadow Circus','','publish','open','open','','shadow-circus','','','2015-09-15 13:37:16','2015-09-15 05:37:16','',0,'http://localhost:8080/wordpress/?p=383',0,'post','',0);
