INSERT INTO `wp_posts` VALUES (411,1,'2015-09-15 19:00:19','2015-09-15 11:00:19','<ul>\n	<li><a target=\"_blank\" href=\"/2015dtd/wp-content/uploads/2015/09/a-1.doc\" rel=\"noopener\">系課程委員會設置要點</a></li>\n	<li><a target=\"_blank\" href=\"/2015dtd/wp-content/uploads/2015/09/a-2.doc\" rel=\"noopener\">系務暨課程自我評鑑實施要點</a></li>\n	<li><a target=\"_blank\" href=\"/2015dtd/wp-content/uploads/2015/09/a-3.doc\" rel=\"noopener\">數位科技設計學系主管選薦辦法</a></li>\n	<li><a target=\"_blank\" href=\"/2015dtd/wp-content/uploads/2015/09/a-4.pdf\" rel=\"noopener\">數位科技設計學系教師評審委員會設置要點</a></li>\n</ul>','行政法規','','publish','open','open','','%e8%a1%8c%e6%94%bf%e6%b3%95%e8%a6%8f','','','2015-09-15 19:00:19','2015-09-15 11:00:19','',0,'http://localhost:8080/wordpress/?p=411',0,'post','',0);
INSERT INTO `wp_posts` VALUES (450,1,'2015-09-16 01:34:11','2015-09-15 17:34:11','[vc_row][vc_column][vc_column_text]\n<h2 style=\"text-align: center;\">COCOPUN!</h2>\n<h3 style=\"text-align: center;\">金孟璇 | 盤芹綺 | 董俐嫻 | 蔡玉婕</h3>\n\n<hr />\n\n<h3 style=\"text-align: center;\"></h3>\n<h2 style=\"text-align: center;\"><strong>遊戲概觀</strong></h2>\n<h4 style=\"text-align: center;\">COCOPUN!是一款結合實體互動的音樂節奏遊戲，</h4>\n<h4 style=\"text-align: center;\">玩家乘坐木箱鼓上，並仔細聆聽遊戲中的樂曲，</h4>\n<h4 style=\"text-align: center;\">當拍打木箱鼓的節奏與提示音符的節奏吻合即可得分，</h4>\n<h4 style=\"text-align: center;\">讓玩家體驗一邊遊玩一邊享受音樂的樂趣!</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>遊戲類型</strong></h2>\n<h4 style=\"text-align: center;\">音樂節奏遊戲</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>設計目標</strong></h2>\n<h4 style=\"text-align: center;\">音樂節奏遊戲在手機平板上十分流行，</h4>\n<h4 style=\"text-align: center;\">但玩法多著墨在觸控螢幕的互動，我們希望加入實體互動的性質，</h4>\n<h4 style=\"text-align: center;\">結合節奏樂器木箱鼓(Cajón)，讓音樂節奏的遊戲性更佳豐富有趣!</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>故事背景</strong></h2>\n<h4 style=\"text-align: center;\">一隻可愛的外星小河童，獨自揹著木箱鼓(Cajón)，</h4>\n<h4 style=\"text-align: center;\">在宇宙眾星之間徘徊選擇，勇敢地前往各星球用音樂探索一切未知的新事物!</h4>\n[/vc_column_text][/vc_column][/vc_row][vc_row][vc_column][vc_btn title=\"回上一頁\" color=\"info\" link=\"url:http%3A%2F%2Fdtd.ntue.edu.tw%2F%25E4%25BD%259C%25E5%2593%2581%25E5%25B1%2595%25E7%25A4%25BA%2F||\"][/vc_column][/vc_row]','COCOPUN!','','publish','open','open','','cocopun','','','2015-09-16 01:34:11','2015-09-15 17:34:11','',0,'http://localhost:8080/wordpress/?p=450',0,'post','',0);
INSERT INTO `wp_posts` VALUES (453,1,'2015-09-16 01:35:48','2015-09-15 17:35:48','[vc_row][vc_column][vc_column_text]\n<h2 style=\"text-align: center;\"><strong>Where is my Ring</strong></h2>\n<h3 style=\"text-align: center;\">張育榮 | 李昀融 | 連慧潔 | 吳彥達 | 陳乃瑋</h3>\n\n<hr />\n\n<h3 style=\"text-align: center;\"></h3>\n<h2 style=\"text-align: center;\"><strong>遊戲概觀</strong></h2>\n<h4 style=\"text-align: center;\">一款Q版風格的冒險遊戲 主角必須在架空的大陸上冒險，</h4>\n<h4 style=\"text-align: center;\">過程是以在地圖上走點的方式呈現，</h4>\n<h4 style=\"text-align: center;\">每到一個關卡會轉變成３Ｄ的遊戲場景，</h4>\n<h4 style=\"text-align: center;\">玩家必須透過不斷過關或是達成任務 提升自己的能力，</h4>\n<h4 style=\"text-align: center;\">來取得神祕的寶石 解開家人消失不見的謎團。</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>遊戲類型</strong></h2>\n<h4 style=\"text-align: center;\">動作冒險遊戲</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>設計目標</strong></h2>\n<h4 style=\"text-align: center;\">希望在一個自己所想像的大陸中進行冒險。</h4>\n<h4 style=\"text-align: center;\">因此，我們將各種不同的元素與所想要的國土結合起來，形成了故事中所需要的都市，</h4>\n<h4 style=\"text-align: center;\">讓玩家在這塊神祕的大陸上探索，並加入過關的要素，</h4>\n<h4 style=\"text-align: center;\">讓玩家在享受故事之餘 還能看見不同風貌的國土與未知的領地</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>故事背景</strong></h2>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">主角法伊發現自己的家人被不知名的人物抓走後，</span></h4>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">在現場找到了些微的線索 爺爺所留下的信紙以及一顆鑲有半月形狀的寶石，</span></h4>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">為了知道這件事的幕後主使者是誰，</span></h4>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">以及家人的去向法伊便帶著爺爺留給法伊的寶物以及提示 踏上了旅程。</span></h4>\n[/vc_column_text][/vc_column][/vc_row][vc_row][vc_column][vc_btn title=\"回上一頁\" color=\"info\" link=\"url:http%3A%2F%2Fdtd.ntue.edu.tw%2F%25E4%25BD%259C%25E5%2593%2581%25E5%25B1%2595%25E7%25A4%25BA%2F||\"][/vc_column][/vc_row]','Where is my Ring','','publish','open','open','','where-is-my-ring','','','2015-09-16 01:35:48','2015-09-15 17:35:48','',0,'http://localhost:8080/wordpress/?p=453',0,'post','',0);
INSERT INTO `wp_posts` VALUES (456,1,'2015-09-16 01:36:59','2015-09-15 17:36:59','[vc_row][vc_column][vc_column_text]\n<h2 style=\"text-align: center;\"> Running Rabbit</h2>\n<h3 style=\"text-align: center;\">孫珮均 | 傅詩涵 | 陳韋任 | 葉昆銘</h3>\n\n<hr />\n\n<h3 style=\"text-align: center;\"></h3>\n<h2 style=\"text-align: center;\"><strong>遊戲概觀</strong></h2>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">遊戲中玩家扮演拯救兔子世界的英雄，</span><span style=\"line-height: 1.5;\">透過音樂的節奏控制兔子的行為，</span></h4>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">玩家必須收集許多胡蘿蔔，</span><span style=\"line-height: 1.5;\">去搶救被感染的兔子。</span></h4>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">操作上主要以四個按鍵搭配不同順序而製造出挑戰性。</span></h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>遊戲類型</strong></h2>\n<h4 style=\"text-align: center;\">音樂節奏遊戲</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>設計目標</strong></h2>\n<h4 style=\"text-align: center;\">簡單 ： 操作上只有四個按鍵，搭配不同順序製造出挑戰性。</h4>\n<h4 style=\"text-align: center;\">獨創音樂 ： 玩家可隨著節奏前進，並正確在節奏上按下指定按鍵獲得胡蘿蔔。</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>故事背景</strong></h2>\n<h4 style=\"text-align: center;\">原本歡樂的瑞彼特星球，突然被瑞彼特星球入侵，</h4>\n<h4 style=\"text-align: center;\">擁有特殊力量的彩兔，成為唯一能拯救世界的英雄，</h4>\n<h4 style=\"text-align: center;\">為了拯救其他中毒的同伴，彩兔開始了它的任務，</h4>\n<h4 style=\"text-align: center;\">瑞彼特就快要消失，彩兔能否拯救成功?</h4>\n<h4 style=\"text-align: center;\"></h4>\n[/vc_column_text][/vc_column][/vc_row][vc_row][vc_column][vc_btn title=\"回上一頁\" color=\"info\" link=\"url:http%3A%2F%2Fdtd.ntue.edu.tw%2F%25E4%25BD%259C%25E5%2593%2581%25E5%25B1%2595%25E7%25A4%25BA%2F||\"][/vc_column][/vc_row]','Running Rabbit','','publish','open','open','','running-rabbit','','','2015-09-16 01:36:59','2015-09-15 17:36:59','',0,'http://localhost:8080/wordpress/?p=456',0,'post','',0);
INSERT INTO `wp_posts` VALUES (462,1,'2015-09-16 14:11:36','2015-09-16 06:11:36','[vc_row][vc_column][vc_column_text]\n<h2 style=\"text-align: center;\"><strong>Dream of Art</strong></h2>\n<h3 style=\"text-align: center;\">宋俊霖</h3>\n\n<hr />\n\n<h3 style=\"text-align: center;\"></h3>\n<h2 style=\"text-align: center;\"><strong>遊戲概觀</strong></h2>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">畫家做了一個夢，夢見自己走進畫中世界，在畫作裡頭遊走。</span></h4>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">在奇幻的夢裡，發生了各種驚險的危機，為了不要醒來，讓夢持續地做下去，</span></h4>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">一場關於藝術之夢的冒險旅程就此展開。</span></h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>遊戲類型</strong></h2>\n<h4 style=\"text-align: center;\">2D橫向卷軸動作冒險遊戲</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>設計目標</strong></h2>\n<h4 style=\"text-align: center;\">以簡單的按鍵操控角色的移動</h4>\n<h4 style=\"text-align: center;\">以不同的筆刷繪製背景呈現畫中世界</h4>\n<h4 style=\"text-align: center;\">設計各種互動元件讓遊戲變得更好玩</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>故事背景</strong></h2>\n<h4 style=\"text-align: center;\">「我進入了這個世界，我畫的這個世界裡。」</h4>\n<h4 style=\"text-align: center;\">踏在油畫顏料畫成的草地上，那觸感並不是現實中草地的柔軟，而是油畫乾掉後的堅硬觸感。</h4>\n<h4 style=\"text-align: center;\">天空藍的很美，天上的白雲有著油畫刻鏤出的流線，它輕輕地飄動著、捲動著。</h4>\n<h4 style=\"text-align: center;\">回頭一看，畫框裡映照的的是我的畫室。</h4>\n<h4 style=\"text-align: center;\">睡著的那個人是我吧，也許我正在做著夢。</h4>\n<h4 style=\"text-align: center;\">夢見自己進入畫裡面實在是很難得的經驗，不知道繼續往前走會有怎麼樣的景象?</h4>\n<h4 style=\"text-align: center;\">眼前出現一頭沉睡的獅子，他醒來，與我四目相接，牠露出一口兇惡的利牙，弓起了背，準備撲向我!</h4>\n<h4 style=\"text-align: center;\">還能怎麼辦?先逃再說吧!</h4>\n[/vc_column_text][/vc_column][/vc_row][vc_row][vc_column][vc_btn title=\"回上一頁\" color=\"info\" link=\"url:http%3A%2F%2Fdtd.ntue.edu.tw%2F%25E4%25BD%259C%25E5%2593%2581%25E5%25B1%2595%25E7%25A4%25BA%2F||\"][/vc_column][/vc_row]','Dream of Art','','publish','open','open','','dream-of-art','','','2015-09-16 14:11:36','2015-09-16 06:11:36','',0,'http://localhost:8080/wordpress/?p=462',0,'post','',0);
INSERT INTO `wp_posts` VALUES (465,1,'2015-09-16 14:13:12','2015-09-16 06:13:12','[vc_row][vc_column][vc_column_text]\n<h2 style=\"text-align: center;\"><strong>URANUS</strong></h2>\n<h3 style=\"text-align: center;\">蘇雅雯 | 周珮琪 | 黃博圓</h3>\n\n<hr />\n\n<h3 style=\"text-align: center;\"></h3>\n<h2 style=\"text-align: center;\"><strong>遊戲概觀</strong></h2>\n<h4 style=\"text-align: center;\">一款可以享受飛行的快感、以及利用Leap motion裝置，</h4>\n<h4 style=\"text-align: center;\">運用簡單手勢使用魔法的遊戲 。</h4>\n<h4 style=\"text-align: center;\">將透過主角的復國熱忱、打敗黑暗邪念，喚醒內心沉睡的熱血少年心!</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>遊戲類型</strong></h2>\n<h4 style=\"text-align: center;\">第一人稱射擊體感遊戲</h4>\n<h4 style=\"text-align: center;\">裝置為Leap Motion</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>設計目標</strong></h2>\n<h4 style=\"text-align: center;\">Unique：利用最新的leap motion，獨特的遊玩方式。</h4>\n<h4 style=\"text-align: center;\">Exciting：3D飛行遊戲，發動魔法的視覺效果，</h4>\n<h4 style=\"text-align: center;\">讓玩家在這塊神祕的大陸上探索，並加入過關的要素，</h4>\n<h4 style=\"text-align: center;\">Feedback：關卡評分制度，完美過關的快感。</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>故事背景</strong></h2>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">看似安祥和諧的蟲蟲國度─烏拉諾斯，其實隱藏在眾蟲內心深處的貪婪、邪念一直存在。</span></h4>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">由先祖封印的怪物們，藉由吸收這些黑暗氣息，打破封印，再度襲捲而來！</span></h4>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">怪物將手無縛雞之力的蟲蟲趕出大陸，使得蟲蟲們只能逃往空中大陸。</span></h4>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">敗戰的打擊，讓原本已所剩不多的正向力量蕩然無存。</span></h4>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">而主角杰拉，卻是一股清流，內心從未被黑暗所遮蔽的他，</span><span style=\"line-height: 1.5;\">因緣際會從主教的手中得到一本魔法秘笈，</span></h4>\n<h4 style=\"text-align: center;\"><span style=\"line-height: 1.5;\">這魔法只能被內心還擁有正向力量的蟲蟲所使用。</span><span style=\"line-height: 1.5;\">於是杰拉招集伙伴，爾後踏上了反攻大陸的旅程…</span></h4>\n[/vc_column_text][/vc_column][/vc_row][vc_row][vc_column][vc_btn title=\"回上一頁\" color=\"info\" link=\"url:http%3A%2F%2Fdtd.ntue.edu.tw%2F%25E4%25BD%259C%25E5%2593%2581%25E5%25B1%2595%25E7%25A4%25BA%2F||\"][/vc_column][/vc_row]','烏拉諾斯','','publish','open','open','','uranus','','','2015-09-16 14:13:12','2015-09-16 06:13:12','',0,'http://localhost:8080/wordpress/?p=465',0,'post','',0);
INSERT INTO `wp_posts` VALUES (468,1,'2015-09-16 14:14:39','2015-09-16 06:14:39','[vc_row][vc_column][vc_column_text]\n<h2 style=\"text-align: center;\">TIME</h2>\n<h3 style=\"text-align: center;\">蔡賜亭 | 李菁琳 | 劉尚 | 王韻涵 | 郭千慧</h3>\n\n<hr />\n\n<h3 style=\"text-align: center;\"></h3>\n<h2 style=\"text-align: center;\"><strong>遊戲概觀</strong></h2>\n<h4 style=\"text-align: center;\">結合關卡制及RPG故事情節，「時間就是金錢」為本作的核心機制。\n「塔米島」上的居民皆擁有固定的生命能量與獨特的天賦能力。</h4>\n<h4 style=\"text-align: center;\">主角奇果背負著復甦島國的使命，在有限的時間內，藉由給予他人生命能量，\n蒐集情報、使用各種天賦解開任務，尋找及拼湊散落各地、庇祐國土的「塔米石」。</h4>\n<h4 style=\"text-align: center;\">究竟事件背後藏有什麼秘密？奇果的身世又是怎麼一回事？一同來探索吧！</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>遊戲類型</strong></h2>\n<h4 style=\"text-align: center;\">2D橫向卷軸RPG益智解謎遊戲\n遊戲平台：平板電腦</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>設計目標</strong></h2>\n<h4 style=\"text-align: center;\">「TIME is MONEY. TIME is LIFE.」為本作宗旨。</h4>\n<h4 style=\"text-align: center;\">遊戲中，時間的運用及分配十分重要，玩家需處理得當方能度過重重難關，\n也藉此宣導珍惜時光、把握當下的概念。</h4>\n<h4 style=\"text-align: center;\">「時間（金錢）的分配」、「天賦的運用」、「抽絲剝繭、娓娓道來的故事」</h4>\n<h4 style=\"text-align: center;\">為三大主軸，豐富遊戲樂趣！</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>故事背景</strong></h2>\n<h4 style=\"text-align: center;\">在一次神秘事件中，支配並供給「塔米島」的「塔米石」遭到封印，\n導致所有的居民只剩下48塔米（單位）的生命能量。\n多年後，一位博士宣稱找到了解除詛咒的方法，著手舉行了儀式。\n不料儀式失敗，所有人的生命開始急速消失並石化！</h4>\n<h4 style=\"text-align: center;\">博士見狀，在自己的塔米消失殆盡前，託付倖存的主角</h4>\n<h4 style=\"text-align: center;\">奇果以及博士的助手－－里多，找出背後的真相並拯救島嶼......</h4>\n[/vc_column_text][/vc_column][/vc_row][vc_row][vc_column][vc_btn title=\"回上一頁\" shape=\"round\" color=\"info\" link=\"url:http%3A%2F%2Fdtd.ntue.edu.tw%2F%25E4%25BD%259C%25E5%2593%2581%25E5%25B1%2595%25E7%25A4%25BA%2F||\"][/vc_column][/vc_row]','Time','','publish','open','open','','time','','','2015-09-16 14:14:39','2015-09-16 06:14:39','',0,'http://localhost:8080/wordpress/?p=468',0,'post','',0);
INSERT INTO `wp_posts` VALUES (473,1,'2015-09-16 14:18:30','2015-09-16 06:18:30','[vc_row][vc_column][vc_column_text]\n<h2 style=\"text-align: center;\">Tree of Justice</h2>\n<h3 style=\"text-align: center;\">楊子寬 | 陳麒仁 | 鄧又偉</h3>\n\n<hr />\n\n<h3 style=\"text-align: center;\"></h3>\n<h2 style=\"text-align: center;\"><strong>遊戲概觀</strong></h2>\n<h4 style=\"text-align: center;\">在當前人手一機的時代 ，市面上卻鮮少以行動裝置操控的免安裝網頁多人遊戲 ，</h4>\n<h4 style=\"text-align: center;\">我們希望藉由人手一機以及多人同時同地操作的優勢增加遊戲的方便性與趣味性 。</h4>\n<h4 style=\"text-align: center;\">並透過這次的專題了解網路在傳輸方面的應用方法與其在實作上會遇到的問題 。</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>遊戲類型</strong></h2>\n<h4 style=\"text-align: center;\">網頁多人連線小遊戲</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>設計目標</strong></h2>\n<h4 style=\"text-align: center;\">用WebGL寫出簡單有趣的網頁遊戲 以行動裝置遠端操控遊戲行為</h4>\n<h4 style=\"text-align: center;\">遊戲著重WebGL的呈現以及遠端操控的技術</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>故事背景</strong></h2>\n<h4 style=\"text-align: center;\">樹人為了保護樹木不被山老鼠盜伐，將自我的靈魂注入了樹群中，</h4>\n<h4 style=\"text-align: center;\">移動樹木藉以閃避山老鼠的盜伐。</h4>\n[/vc_column_text][/vc_column][/vc_row][vc_row][vc_column][vc_btn title=\"回上一頁\" shape=\"round\" color=\"info\" link=\"url:http%3A%2F%2Fdtd.ntue.edu.tw%2F%25E4%25BD%259C%25E5%2593%2581%25E5%25B1%2595%25E7%25A4%25BA%2F||\"][/vc_column][/vc_row]','Tree of Justice','','publish','open','open','','tree-of-justice','','','2015-09-16 14:18:30','2015-09-16 06:18:30','',0,'http://localhost:8080/wordpress/?p=473',0,'post','',0);
INSERT INTO `wp_posts` VALUES (476,1,'2015-09-16 14:22:03','2015-09-16 06:22:03','[vc_row][vc_column][vc_column_text]\n<h2 style=\"text-align: center;\">I2 Science</h2>\n<h3 style=\"text-align: center;\">吃小男孩（Eat Boys）</h3>\n\n<hr />\n\n<h3 style=\"text-align: center;\"></h3>\n<h2 style=\"text-align: center;\"><strong>得獎紀錄</strong></h2>\n<h4 style=\"text-align: center;\">2013 4C數位創作競賽 遊戲應用組  大會金獎、廠商票選獎-最佳作品</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>遊戲概觀</strong></h2>\n<h4 style=\"text-align: center;\">Cocos2d-x  跨平台函式庫為開發基礎本教材</h4>\n<h4 style=\"text-align: center;\">可以同時在三大平板的作業環境上執行</h4>\n<h4 style=\"text-align: center;\">I2Science代表有趣的(Interesting)、互動的(Interactive)自然科學</h4>\n<h4 style=\"text-align: center;\">涵蓋國小自然與生活科技領域的力與運動以及簡單機械等共10個單元</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>遊戲類型</strong></h2>\n<h4 style=\"text-align: center;\">以紙張素材做背景並且以探險日誌的風格來呈現添加有趣的互動，</h4>\n<h4 style=\"text-align: center;\">讓使用者在操作時像是在翻一本具有魔法的電子書</h4>\n\n<hr />\n\n<h4 style=\"text-align: center;\"></h4>\n<h2 style=\"text-align: center;\"><strong>設計目標</strong></h2>\n<h4 style=\"text-align: center;\">使學童能在遊戲的過程中也達到學習的效果提供合適的互動性來闡述該原理的概念</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>故事背景</strong></h2>\n<h4 style=\"text-align: center;\">在一個類似地球的外星世界上，創造具有各自的特色與獨特造型的角色</h4>\n<h4 style=\"text-align: center;\">透過這些角色的有趣互動來呈現每一個科學原理的知識</h4>\n[/vc_column_text][/vc_column][/vc_row][vc_row][vc_column][vc_btn title=\"回上一頁\" shape=\"round\" color=\"info\" link=\"url:http%3A%2F%2Fdtd.ntue.edu.tw%2F%25E4%25BD%259C%25E5%2593%2581%25E5%25B1%2595%25E7%25A4%25BA%2F||\"][/vc_column][/vc_row]','I2 Science','','publish','open','open','','i2-science','','','2015-09-16 14:22:03','2015-09-16 06:22:03','',0,'http://localhost:8080/wordpress/?p=476',0,'post','',0);
INSERT INTO `wp_posts` VALUES (479,1,'2015-09-16 14:25:22','2015-09-16 06:25:22','[vc_row][vc_column][vc_column_text]\n<h2 style=\"text-align: center;\">MORZ</h2>\n<h3 style=\"text-align: center;\">韓秉軒 | 董俐嫺 | 金孟璇 | 巫家瑜 | 蔡玉婕</h3>\n\n<hr />\n\n<h3 style=\"text-align: center;\"></h3>\n<h2 style=\"text-align: center;\"><strong>得獎紀錄</strong></h2>\n<h4 style=\"text-align: center;\">2013台北市原創商品輔導徵選比賽 企劃通過徵選</h4>\n<h4 style=\"text-align: center;\">，在台北市集思會議中心展覽</h4>\n\n<hr />\n\n<h4 style=\"text-align: center;\"></h4>\n<h2 style=\"text-align: center;\"><strong>遊戲概論</strong></h2>\n<h4 style=\"text-align: center;\">本作品為三隻魔滋小型互動公仔，</h4>\n<h4 style=\"text-align: center;\">玩家可以運用android手機無線操控公仔進行音樂指揮，</h4>\n<h4 style=\"text-align: center;\">使之產生不同和音效果</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>遊戲類型</strong></h2>\n<h4 style=\"text-align: center;\">小型互動裝置+行動APP</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>設計目標</strong></h2>\n<h4 style=\"text-align: center;\">本作品為三隻魔滋小型互動公仔，玩家可以運用android手機無線操控公仔進行音樂指揮，使之產生不同和音效果</h4>\n\n<hr />\n\n<h2 style=\"text-align: center;\"><strong>故事背景</strong></h2>\n<h4 style=\"text-align: center;\">三隻小動物決定合唱一首美妙的樂曲給大家欣賞</h4>\n[/vc_column_text][/vc_column][/vc_row][vc_row][vc_column][vc_btn title=\"回上一頁\" shape=\"round\" color=\"info\" link=\"url:http%3A%2F%2Fdtd.ntue.edu.tw%2F%25E4%25BD%259C%25E5%2593%2581%25E5%25B1%2595%25E7%25A4%25BA%2F||\"][/vc_column][/vc_row]','MORZ','','publish','open','open','','morz','','','2015-09-16 14:25:22','2015-09-16 06:25:22','',0,'http://localhost:8080/wordpress/?p=479',0,'post','',0);
