INSERT INTO `wp_posts` VALUES (503,1,'2009-10-05 05:54:56','2009-10-04 21:54:56','請點選下載公告及通知內容，試作物樣板請按滑鼠右鍵另存目標，謝謝。<img class=\" size-full wp-image-195\" alt=\"alt\" src=\"http://localhost/wordpress3/wp-content/uploads/2009/10/shades_smile.gif\" width=\"19\" height=\"19\" /><br /> <ul>     <li>初選投票結果</li>     <li>試作物繳交說明</li>     <li>試作物樣板1(海報)</li>     <li><a href=\"/images/dtd_data/download_table/web_layout.bmp\">試作物樣板2(網站)</a></li> </ul>','本系系徽設計比賽初選投票結果公告及決選試作物收件通知','','publish','open','open','','2009-10-05-05-59-18','','','2009-10-05 05:54:56','2009-10-04 21:54:56','',0,'http://localhost/wordpress3/?p=196',107,'post','',0);
INSERT INTO `wp_posts` VALUES (504,1,'2009-10-20 02:21:55','2009-10-19 18:21:55','<a href=\"http://www.icttoy.org.tw/html/history/index.aspx\">http://www.icttoy.org.tw/html/history/index.aspx</a><br /> <br /> <span id=\"L_class1\"> <td valign=\"top\" width=\"1\"> </td>           <img height=\"176\" alt=\"alt\" width=\"160\" src=\"http://www.icttoy.org.tw/upload/%E8%9B%90%E9%AC%A5%E7%A8%8B%E5%BC%8F-002.png\" />               <td valign=\"top\"> </td>           <table cellspacing=\"0\" cellpadding=\"3\" summary=\"summary\" border=\"0\">     <tbody>         <tr>             <td valign=\"top\" nowrap=\"nowrap\"><strong>作品名稱</strong></td>             <td><span lang=\"EN-GB\" style=\"font-size: 9pt; font-family: Arial\"><span style=\"font-size: 10pt; font-family: 細明體\">蛐鬥程式</span></span></td>         </tr>         <tr>             <td valign=\"top\" nowrap=\"nowrap\"><strong>設計師</strong></td>             <td><span lang=\"EN-GB\" style=\"font-size: 9pt; font-family: Arial\"><span style=\"font-size: 10pt; font-family: 細明體\">高全慶</span></span></td>         </tr>         <tr>             <td valign=\"top\" nowrap=\"nowrap\"><strong>作品說明</strong></td>             <td><span style=\"font-size: 10pt; font-family: 新細明體\"><span style=\"font-size: 9pt; font-family: 新細明體\">『</span>蛐鬥程式<span style=\"font-size: 9pt; font-family: 新細明體\">』</span></span><span style=\"font-size: 10pt; font-family: 新細明體\">以療傷性質之攜帶數位電子互動寵物為切入點，對象為現今繁忙社會中之上班族群。藉由使用者操作電腦介面軟體模擬教育養成寵物實體，開啟藍芽手機無線傳輸裝置連接</span><span lang=\"EN-US\" style=\"font-size: 10pt; font-family: Arial\">JAVA</span><span style=\"font-size: 10pt; font-family: 新細明體\">程式與數位寵物間進行互動溝通與對戰。</span></td>         </tr>     </tbody> </table> </span>','【狂賀】本系巴白山老師指導玩遊碩二高全慶同學獲2009 ICT創意科技玩具設計競賽銀牌獎','','publish','open','open','','2009-ict','','','2009-10-20 02:21:55','2009-10-19 18:21:55','',0,'http://localhost/wordpress3/?p=198',86,'post','',0);
