INSERT INTO `wp_posts` VALUES (524,1,'2010-05-11 17:20:16','2010-05-11 09:20:16','<div><span style=\"color: black\">依本次比賽評選方式公告初選入圍名單，共計</span><span style=\"color: black\">10</span><span style=\"color: black\">名參賽作品入圍，請下載</span><span style=\"color: blue\">入圍名單</span><span style=\"color: black\">。</span></div> <div> </div> <div><span style=\"color: black\">公開票選時間</span><span style=\"color: black\"> : 6<span>月</span>8<span>日</span>(</span><span style=\"color: black\">星期二</span><span style=\"color: black\">)</span><span style=\"color: black\">早上</span><span style=\"color: black\">10:00 ~ </span><span style=\"color: black\">下午</span><span style=\"color: black\">17:00</span></div> <div><span style=\"color: black\">公開票選地點</span><span style=\"color: black\"> : </span><span style=\"color: black\">至善樓</span><span style=\"color: black\">地下川堂</span></div> <div> </div> <div><span style=\"color: black\">當天將有入選的</span><span style=\"color: black\">10</span><span style=\"color: black\">件作品實機操作展示及解說，歡迎全校同學於當日憑學生證領取投票單投下你所支持的作品。</span></div>','數位多媒體創意教材設計比賽 初選審查入圍名單','','publish','open','open','','2010-05-11-17-21-09','','','2010-05-11 17:20:16','2010-05-11 09:20:16','',0,'http://localhost/wordpress3/?p=246',97,'post','',0);
