INSERT INTO `wp_posts` VALUES (588,1,'2011-10-11 03:39:41','2011-10-10 19:39:41','<span style=\"font-size: 14px\">本系蕭瑛東主任、俞齊山老師及資訊科學系許佳興老師指導玩遊碩士班學生周湘寧、韓秉軒及資訊科學系碩士班學生吳昱霆、林廷達參加教育部資訊軟體人才培育計畫行動終端應用資源中心舉辦之期末教學研討會暨實務專題觀摩競賽，以作品「BottleNet」榮獲佳作!!<br /> <br /> 數位系(含玩遊碩士班)全體師生敬賀</span>','【狂賀】本系玩遊碩士班學生參加教育部期末教學研討會暨實務專題觀摩競賽榮獲佳作','','publish','open','open','','2011-10-11-03-49-37','','','2011-10-11 03:39:41','2011-10-10 19:39:41','',0,'http://localhost/wordpress3/?p=384',59,'post','',0);
