INSERT INTO `wp_posts` VALUES (704,1,'2013-02-25 06:22:03','2013-02-24 22:22:03','<div>系辦誠徵101學年度第二學期系辦工讀生<br />  </div> <div><span style=\"background: yellow\">於</span><span style=\"background: yellow\">2/26(</span><span style=\"background: yellow\">星期二</span><span style=\"background: yellow\">)及2/27(星期三)</span><span style=\"background: yellow\">兩日中午12:00</span><span style=\"background: yellow\">至13:30開放登記。</span><br /> <br /> <p class=\"MsoNormal\"><span style=\"font-family:\">篩選排班依據</span><span lang=\"EN-US\">1.</span><span style=\"font-family:\">是否曾在系辦工讀</span><span lang=\"EN-US\">2.</span><span style=\"font-family:\">過去工讀表現</span><span lang=\"EN-US\">3.</span><span style=\"font-family:\">可配合之空堂時間</span></p> <p class=\"MsoNormal\"><span lang=\"EN-US\"> </span><span style=\"font-family:\">調查時段分為週一至週五的上下午時段，每時段限填</span><span lang=\"EN-US\">3</span><span style=\"font-family:\">位同學，每位同學限填</span><span lang=\"EN-US\">2</span><span style=\"font-family:\">個時段，以供排班之依據。</span></p> <p class=\"MsoNormal\"><span lang=\"EN-US\"> </span><span style=\"font-family:\">每時段各排一位同學，排班結果預計於第二週公佈。</span></p> </div> <br />','徵 101學年度第二學期數位系辦工讀生','','publish','open','open','','101-6','','','2013-02-25 06:22:03','2013-02-24 22:22:03','',0,'http://localhost/wordpress3/?p=616',61,'post','',0);
