INSERT INTO `wp_posts` VALUES (709,1,'2013-05-24 01:41:02','2013-05-23 17:41:02','<p><span style=\"font-size: 14px\">本校數位科技設計學系學生<span style=\"color: black\">金克杰、趙姮安<br /> </span><span style=\"color: black\">在2013年新一代設計展</span><span style=\"color: black\">以</span><span style=\"color: black\">作品「</span><span style=\"color: black\"> CELL,Brick</span><span style=\"color: black\">」</span><span style=\"color: black\">獲得</span><span style=\"color: black\">台南創意新人獎入圍獎<br /> </span><span style=\"color: black\">指導老師</span><span style=\"color: black\">：黃聰耀教授、蕭瑛東教授<br /> <br /> 數位系/玩遊碩士班 全體師生 敬賀</span></span></p>','【狂賀】本系大學部學生金克杰、趙姮安於2013新一代設計展榮獲台南創意新人獎入圍獎','','publish','open','open','','2013-2','','','2013-05-24 01:41:02','2013-05-23 17:41:02','',0,'http://localhost/wordpress3/?p=621',22,'post','',0);
