INSERT INTO `wp_posts` VALUES (711,1,'2013-05-29 00:50:28','2013-05-28 16:50:28','<div><span style=\"font-size: 14px\">本系玩遊碩士班學生參加2013年全國智慧型機器人大賽，榮獲佳作!!</span><span style=\"font-size: 14px\"> </span></div> <div><span style=\"font-size: 14px\">參賽學生：陳哲正、曾世佳、陳禹妁、鄭寶琳</span></div> <span style=\"font-size: 14px\">指導教授：巴白山教授、楊孟哲教授<br /> <br /> 數位系/玩遊碩班 全體師生 敬賀</span>','【狂賀】本系玩遊碩士班學生參加2013年全國智慧型機器人大賽榮獲佳作','','publish','open','open','','2013-sp-19875','','','2013-05-29 00:50:28','2013-05-28 16:50:28','',0,'http://localhost/wordpress3/?p=623',20,'post','',0);
INSERT INTO `wp_posts` VALUES (712,1,'2013-05-31 06:15:36','2013-05-30 22:15:36','<p><span style=\"font-size: 12px\"><span style=\"color: black\">一、<b>報到時間：</b></span><b><span style=\"color: black\">102</span></b><b><span style=\"color: black\">年</span></b><b><span style=\"color: black\">6</span></b><b><span style=\"color: black\">月</span></b><b><span style=\"color: black\">1</span></b><b><span style=\"color: black\">日（星期六）上午</span></b><b><span style=\"color: black\">8</span></b><b><span style=\"color: black\">時</span></b><b><span style=\"color: black\">50</span></b><b><span style=\"color: black\">分起，<u>至遲須於面試叫號前完成報到手續</u></span></b><span style=\"color: black\">。</span></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\">二、報到地點：本校科學館</span><span style=\"color: black\">5</span><span style=\"color: black\">樓</span><span style=\"color: black\">507</span><span style=\"color: black\">室。</span></span></p> <p><span style=\"font-size: 12px\"><b><span style=\"color: black\">三、應繳驗證件資料（正本）：<u>國民身分證</u></span></b><b><span style=\"color: black\">。</span></b></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\">四、報到流程：</span></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\"> </span><span style=\"color: black\">（一）請於報到時間內至本校科學館</span><span style=\"color: black\">5</span><span style=\"color: black\">樓報到，<b>逾時不受理報到，未辦理報到者視同放棄面試資格。</b></span></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\"> </span><span style=\"color: black\">（二）完成報到手續後，請依現場服務人員指示至休息區等候。</span></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\"> </span><span style=\"color: black\">（三）面試於上午</span><span style=\"color: black\">9:00</span><span style=\"color: black\">起進行，面試完成後始得離開面試試場。</span></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\">五、攜帶電子通訊用品（含行動電話、呼叫器或具通訊功能之任何電子用品）者請務必關機。</span></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\">六、其他注意事項：</span></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\">（一）<b><u>面試順序於</u></b></span><b><u><span style=\"color: black\">102</span></u></b><b><u><span style=\"color: black\">年</span></u></b><b><u><span style=\"color: black\">5</span></u></b><b><u><span style=\"color: black\">月</span></u></b><b><u><span style=\"color: black\">3</span></u></b><b><u><span style=\"color: black\">1</span></u></b><b><u><span style=\"color: black\">日（星期五）公告於系所網頁，經公告後不予調整。</span></u></b></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\">（二）請於面試前</span><span style=\"color: black\">30</span><span style=\"color: black\">分鐘提早至考場等候，<b><u>面試前叫號三次未到者，視同放棄面試資格</u></b>。面試時間為</span><span style=\"color: black\">10</span><span style=\"color: black\">分鐘（含</span><span style=\"color: black\">3</span><span style=\"color: black\">分鐘器材設置準備），於第</span><span style=\"color: black\">9</span><span style=\"color: black\">分鐘時響鈴</span><span style=\"color: black\">1</span><span style=\"color: black\">短聲，於第</span><span style=\"color: black\">10</span><span style=\"color: black\">分鐘時響鈴</span><span style=\"color: black\">3</span><span style=\"color: black\">短聲，以提醒考生面試時間已到，請立即離開面試場地。</span></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\">（三）為不影響其他考生之權益，禁止在考場範圍內隨意走動，並請於面試完畢後依照現場服務人員指示立即離開試場，切勿返回休息區等待。</span></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\">七、本系辦公室電話：（</span><span style=\"color: black\">02</span><span style=\"color: black\">）</span><span style=\"color: black\">2732-1104 </span><span style=\"color: black\">分機</span><span style=\"color: black\">63533 </span><span style=\"color: black\">陳珮如助教</span></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\">本系辦公室地址：</span><span style=\"color: black\">(106)</span><span style=\"color: black\">台北市大安區和平東路</span><span style=\"color: black\">2</span><span style=\"color: black\">段</span><span style=\"color: black\">134</span><span style=\"color: black\">號科學館</span><span style=\"color: black\">5</span><span style=\"color: black\">樓</span><span style=\"color: black\">507</span><span style=\"color: black\">室</span></span></p> <p><span style=\"font-size: 12px\"><span style=\"color: black\">本系所網址：</span><span style=\"color: blue\">http://dtd.ntue.edu.tw/</span></span></p>','102學年度玩具與遊戲設計碩士學位在職進修專班招生考試第2階段口試時間表','','publish','open','open','','1022-sp-30220','','','2013-05-31 06:15:36','2013-05-30 22:15:36','',0,'http://localhost/wordpress3/?p=624',23,'post','',0);
INSERT INTO `wp_posts` VALUES (713,1,'2013-06-03 09:50:16','2013-06-03 01:50:16','<div><span style=\"font-size: 14px\">本系大學部及玩遊碩士班學生以「阿卡貝拉小型互動電子公仔」通過臺北數位內容產業整備接軌輔導專案─原創商品化輔導決賽徵選!!</span></div> <div><span style=\"font-size: 14px\">製作團隊：金孟璇、董俐嫺、蔡玉婕、巫家瑜、韓秉軒</span></div> <span style=\"font-size: 14px\">指導老師：蕭瑛東教授、俞齊山教授<br /> <br /> 數位系/玩遊碩班 全體師生 敬賀</span>','【狂賀】本系大學部及玩遊碩士班學生通過原創商品化輔導決賽徵選','','publish','open','open','','2013-06-03-09-51-01','','','2013-06-03 09:50:16','2013-06-03 01:50:16','',0,'http://localhost/wordpress3/?p=625',19,'post','',0);
