INSERT INTO `wp_posts` VALUES (3841,1,'2021-05-04 08:47:53','2021-05-04 00:47:53','a:13:{s:4:\"type\";s:6:\"select\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:1;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:7:\"choices\";a:5:{s:18:\"互動科技領域\";s:18:\"互動科技領域\";s:18:\"遊戲設計領域\";s:18:\"遊戲設計領域\";s:18:\"創意設計領域\";s:18:\"創意設計領域\";s:12:\"兼任老師\";s:12:\"兼任老師\";s:12:\"行政人員\";s:12:\"行政人員\";}s:13:\"default_value\";b:0;s:10:\"allow_null\";i:0;s:8:\"multiple\";i:0;s:2:\"ui\";i:0;s:13:\"return_format\";s:5:\"value\";s:4:\"ajax\";i:0;s:11:\"placeholder\";s:0:\"\";}','教師分組','grouptitle','publish','closed','closed','','field_609097092d853','','','2021-05-04 08:47:53','2021-05-04 00:47:53','',0,'http://dtd.ntue.edu.tw:8080/?post_type=acf-field&amp;p=3841',0,'acf-field','',0);
